import React, { useEffect, useState } from 'react'
import "./Login.css"
import { useNavigate } from 'react-router-dom'
const Login = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    useEffect(()=>{
        const auth = localStorage.getItem("user");
        if (auth){
            navigate("/");
        }
    })

    const handleLogin = async () =>{
        console.log(email, password);
        const data = await fetch("http://localhost:5000/login", {
            method: "post",
            body: JSON.stringify({email, password}),
            headers: {
                "Content-Type": "application/json"
            } 
        })
        const results = await data.json;
        console.log(results);
        if (results.name){
            localStorage.setItem("user", JSON.stringify(results))
            navigate("/")
        }
        else{
            alert("please enter correct details")
        }
    }
  return (
    <div className='login'>
        <h1>Log In</h1>
      <input type="text" className='inputBox' value={email} onChange={(e)=>{setEmail(e.target.value)}} placeholder='Enter Email'/>
      <input type="passowrd" className='inputBox' value={password} onChange={(e)=>{setPassword(e.target.value)}} placeholder='Enter password '/>
      <button onClick={handleLogin} className="btn" type="button">
        Login
      </button>
    </div>
  )
}

export default Login
